using UnityEngine;public class EditorGameManager{		private static EditorGameManager _instance;	private Wiimote detectedWiiMote;	private Furniture[] furnitures;	private bool isGrabbingObject = false;	private GameObject grabbedObject;		private EditorGameManager()	{		detectedWiiMote = null;	}		public static EditorGameManager Instance 	{		get 		{			if (_instance == null)				_instance = new EditorGameManager ();			return _instance;		}	}		//Detected Wiimote	public Wiimote CurrentWiimote	{		get		{			return detectedWiiMote;		}		set		{			detectedWiiMote = value;		}	}		public Furniture[] Furnitures {		get {			return this.furnitures;		}		set {			this.furnitures = value;		}	}		public bool IsGrabbingObject {		get {			return isGrabbingObject;		}		set {			isGrabbingObject = value;		}	}		public GameObject GrabbedObject {		get {			return grabbedObject;		}		set {			grabbedObject = value;		}	}}